import React, {useEffect, useState} from "react";
import PostGuestBook from "../../components/firebase/postGuestBook";
import GetGuestBook from "../../components/firebase/getGuestBook";
import './guestBook.css';

export default function GuestBook() {
    const [writer, setWriter] = useState('');
    const [content, setContent] = useState('');
    const [inputData, setInputData] = useState([]);

    // const [clicked, setClicked] = useState(false);
    useEffect(() => {
        exec();
        // console.log('guestInput : ',inputData);
    }, [])

    async function exec(){
        const data =  await GetGuestBook();
        await setInputData(data);
    }

    const onWriterChange = (e) => {
        setWriter(e.target.value);
    };

    const onContentChange = (e) => {
        setContent(e.target.value);
    };
    const onPost = async () => {
        await PostGuestBook(writer, content);
        // console.log(url);
    };
    // async function onPost() {
    //     await PostGuestBook(writer, content);
    //     // setClicked(true);
    // };

    return (
        <>
            <div className="guestBook_main">
                <span className="guestBook_title"> 방명록 </span>
                <div className="guest_list">
                    {/*<ul className="guest_list">*/}
                    {/*<li className="grid_list">*/}
                    <div className="list_box">
                        <input className="input_name" placeholder="이름" onChange={onWriterChange}/>
                        <input className="input_content" placeholder="내용 작성란입니다. 비방, 욕설, 광고글 작성 시 삭제될 수 있습니다." onChange={onContentChange}/>
                        <button className="input_btn" onClick={onPost}>등록</button>
                    </div>
                    {/*</li>*/}
                    {
                        inputData.map((item, index) => {
                            if (inputData.length > 0) {
                                return(
                                    // <li className="grid_list">
                                    <div className="list_box">
                                        <div className="guest_name">
                                            <span>{item.writer}</span>
                                        </div>
                                        <div className="guest_content"> {item.content} </div>
                                    </div>
                                    // </li>

                                )
                            }
                        })
                    }
                    {/*<div style={style.outerBox}>*/}
                    {/*    */}
                    {/*</div>*/}
                    {/*</div>*/}
                    {/*    </ul>*/}
                </div>
            </div>

        </>
    )
}

const style = {
    main : {
        width: '100vw',
        overflow: 'auto',
        height: 'auto',
        display: 'grid',
        // flexDirection: 'row',
        gridColumnGap: '23px',
        gridRowGap: '50px',
        gridTemplateColumns : 'repeat(2, 1fr)',
        minHeight: '100vh'

    },
    guest_input : {
        width : '330px',
        height : '330px',
        display: 'flex',
        backgroundColor: '#C4C4C4',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center'

    },
    guest_name : {
        width: '290px',
        height: '30px'
    },
    guest_book : {
        width : '290px',
        height : '200px'
    },
    content: {
        width: '330px',
        height: '330px',
        fontsize: '18px',
        backgroundColor: '#C4C4C4',
        margin: '20px',
        textAlign: 'center',
        display: 'grid',
        gridRowGap: '50px',
        gridTemplateColumns : 'repeat(2, 1fr)',

    },
    outerBox: {
        display: 'flex',
        flexDirection: 'row',
    }
}
