import firebase from "./firebase-manager";

const db = firebase.firestore();

export async function GetPostingInfo(url) {
    const res = await db.collection("writing").doc(url).then(async (doc)=> {
        if (doc.exists) {
            const docData = doc.data();
            return docData;
        }
    })
    return res;
}
