import React, {useState} from "react";
import {useHistory} from "react-router-dom";
import Draggable from "react-draggable";
import threeD from "./img/3D.png";

export default function Animation() {
    const history = useHistory();

    const [position, setPosition] = useState({ x: 0, y: 0 }); // box의 포지션 값
    // 업데이트 되는 값을 set 해줌
    const trackPos = (data) => {
        setPosition({ x: data.x, y: data.y });
    };

    const onClick = () => {
        history.push(`/works/3D`);
    }

    return(
        <div>
            <div style={style.main_body} className="link_body_com">
                <Draggable
                    onDrag={(e, data) => trackPos(data)}
                >
                    <div className="team_3D"  onDoubleClick={onClick} onTouchStart={onClick}>
                        <img className="team_3D_img" src={threeD}/>
                        <p style={style.p} className="team_3D_title">3D</p>
                    </div>
                </Draggable>
            </div>
            <div style={style.main_body} className="link_body_mobile">
                <div className="team_3D"  onClick={onClick}>
                    <img className="team_3D_img" src={threeD}/>
                    <p style={style.p} className="team_3D_title">3D</p>
                </div>
            </div>
        </div>
    )
}

const style = {
    main_body: {
        textAlign:'center',
        width: '130px',
        height: '120px',
    },
    box: {
        width: '130px',
        height: '100px',
        backgroundColor: "red",
        margin:'0',
        borderRadius:"10%",
        // textAlign:'center',
    },
    p: {
        margin: '0',
        fontsize: '14px',
        color:"white"
    }
}
