import React, {useEffect, useState} from "react";
import PostGuestBook from "../../components/firebase/postGuestBook";
import GetGuestBook from "../../components/firebase/getGuestBook";
import {postWorks} from "../../components/firebase/postWorks";
import PostThumbNail from "../../components/firebase/postThumbNail";

export default function GuestBook() {
    const [writer, setWriter] = useState('');
    const [content, setContent] = useState('');
    const [inputData, setInputData] = useState([]);

    // const [clicked, setClicked] = useState(false);
    useEffect(() => {
        exec();
        // console.log('guestInput : ',inputData);
    }, [])

    async function exec(){
        const data =  await GetGuestBook();
        await setInputData(data);
    }

    const onWriterChange = (e) => {
        setWriter(e.target.value);
    };

    const onContentChange = (e) => {
        setContent(e.target.value);
    };
    const onPost = async () => {
        await PostGuestBook(writer, content);
        // console.log(url);
    };
    // async function onPost() {
    //     await PostGuestBook(writer, content);
    //     // setClicked(true);
    // };

    return (
        <>
            <div style={style.main}>
                <div style={style.guest_input} className="guest_input">
                    <input style={style.guest_name} placeholder="이름" onChange={onWriterChange}/>
                    <textarea style={style.guest_book} placeholder="내용 작성란입니다.&#13;&#10;비방, 욕설, 광고글 작성 시 삭제될 수 있습니다." onChange={onContentChange}/>
                    <button onClick={onPost}>등록</button>
                </div>
                <div style={style.outerBox}>
                    {
                        inputData.map((item, index) => {
                            if (inputData.length > 0) {

                                return(
                                    <div style={style.content}>
                                        <div style={style.guest_name}>
                                            <p>{item.writer}</p>
                                        </div>
                                        <div style={style.guest_book}>
                                            <p>{item.content}</p>
                                        </div>
                                    </div>
                                )
                            }
                        })
                    }
                </div>
            </div>

        </>
    )
}

const style = {
    main: {
        width: '100vw',
        overflow: 'auto',
        height: 'auto',
        display: 'flex',
        flexDirection: 'row',
        gridColumnGap: '23px',
        gridRowGap: '50px',
        gridTemplateColumns : 'repeat(3, 1fr)',
        minHeight: '100vh'

    },
    guest_input : {
        width : '330px',
        height : '330px',
        display: 'flex',
        backgroundColor: '#C4C4C4',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center'

    },
    guest_name : {
        width: '290px',
        height: '30px'
    },
    guest_book : {
        width : '290px',
        height : '200px'
    },
    content: {
        width: '330px',
        height: '330px',
        fontsize: '18px',
        backgroundColor: '#C4C4C4',
        margin: '20px',
        textAlign: 'center'
    },
    outerBox: {
        display: 'flex',
        flexDirection: 'row',
    }
}
