import './HomeView.css';
import React, {useState} from "react";

import DecoIMG from "./decoIMG";
import InstaLink from "../../components/home/items/instaLink";
import YoutubeLink from "../../components/home/items/youtubeLink";

import Admin from "../../components/home/items/admin";
import Organization from "../../components/home/items/organization";
import Guest from "../../components/home/items/guest";
import Professor from "../../components/home/items/professor";
import Banner from "../../components/home/items/banner";

import MotionGraphics from "../../components/home/team/MotionGraphics";
import Animation from "../../components/home/team/Animation";
import VFX_SFX from "../../components/home/team/VFX_SFX";
import Game from "../../components/home/team/Game";
import InterActionDesign from "../../components/home/team/InterActionDesign";
import WebtoonConceptArt from "../../components/home/team/WebtoonConceptArt";
import Notice from "../../components/home/items/notice";
import Trash from "../../components/home/items/trash";

import Draggable from "react-draggable";

import deco1 from './img/typo_poster.png';
import deco2 from './img/photo_final2222.png';

function HomeView() {
    const [position, setPosition] = useState({ x: 0, y: 0 });

    const trackPos = (data) => {
        setPosition({ x: data.x, y: data.y });
    };

    const [status, setStatus] = useState(false)
    const openModal = () => {
        setStatus(!status);
    };

    const closeModal = () => {
        if (status == true) {
            setStatus(false);
        }
    }
    return (
        <>
            <div className="hommeWrap">
                <div className="items">
                    <InstaLink/>
                    <YoutubeLink/>
                    <Notice/>
                    <Trash/>
                        <div className="deco_image">
                            <div className="four_deco_image">
                                <div id="deco_1"><div onClick={closeModal}>
                                    <div className="main_body" style={style.main_body}>
                                        <Draggable onDrag={(e, data) => trackPos(data)} >
                                            <div className="home_type" style={style.deco_image} onDoubleClick={openModal}>
                                                <img src={deco1} className="home_type_img"/>
                                                <p style={style.p} className="home_type_title">typo_poster.jpg</p>
                                            </div>
                                        </Draggable>
                                        {status ? (
                                            <div onClick={closeModal} className="pop_image" style={style.set_body}>
                                                <img onClick={closeModal} style={style.set_box} src={deco1}/>
                                            </div>
                                        ):null}
                                    </div>
                                </div>
                                <div onClick={closeModal}>
                                    <div className="main_body" style={style.main_body}>
                                        <Draggable onDrag={(e, data) => trackPos(data)} >
                                            <div className="home_photo" style={style.deco_image} onDoubleClick={openModal}>
                                                <img src={deco2} className="home_photo_img"/>
                                                <p style={style.p} className="home_photo_title">photo_final2222.jpg</p>
                                            </div>
                                        </Draggable>
                                        {status ? (
                                            <div onClick={closeModal} className="pop_image" style={style.set_body}>
                                                <img onClick={closeModal} style={style.set_box} src={deco2}/>
                                            </div>
                                        ):null}
                                    </div>
                                </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="pages">
                            <Admin/>
                            <Organization/>
                            <Guest/>
                            <Professor/>
                            <Banner/>
                    </div>
                    <div className="team" >
                        <MotionGraphics/>
                        <Animation/>
                        <VFX_SFX/>
                        <Game/>
                        <InterActionDesign/>
                        <WebtoonConceptArt/>
                    </div>
            </div>
        </>
    );
}
export default HomeView;


const style = {
    main_body: {
        textAlign:'center',
        width: '120px',
        height: 'auto',
    },
    box: {
        width: '120px',
        height: '120px',
        backgroundColor: "red",
        margin:'0',
        borderRadius:"100%"
    },
    set_box: {
        width: '80vw',
        height: '80vh',
        backgroundColor: "blue",
        position: 'fixed',
        top: '16vh',
        left: '15vw',

        zIndex: '100'
    },
    set_body: {
        display: 'flex',
    },
    p: {
        margin: '0',
        color:"white"
    },
    deco_image: {
        position: 'relative',
        left: '50%',
        zIndex:'1',
        // width: '100%'
    }
}
