import React from "react";

export default function GuestBook() {
    return (
        <>
            <div style={style.guest_input} className="guest_input">
                <input style={style.guest_name} placeholder="이름"/>
                <input style={style.guest_book} placeholder="비방, 욕설, 광고 글 작성 시 삭제될 수 있습니다."/>
                <button>등록</button>
            </div>
        </>
    )
}

const style = {
    guest_input : {
        width : '330px',
        height : '330px',
        display: 'flex',

    },
    guest_name : {
        width: '290px',
        height: '40px'
    },
    guest_book : {
        width : '290px',
        height : '200px'
    }
}
