import firebase from "./firebase-manager";

const db = firebase.firestore();

export async function GetPostingInfo(url) {
    const docRef = db.collection("writing").doc(url);
    function getting() {docRef.get().then((doc)=>{
        if (doc.exists) {
            console.log(doc.data());
            return doc.data();
        } else {return null;}
    }).catch((error)=>{
        console.log(error);
    })}
}
