import Draggable from "react-draggable";
import React, {useState} from "react";

import Modal from "./Modal";

export default function DecoIMG() {
    const [position, setPosition] = useState({ x: 0, y: 0 }); // box의 포지션 값
    // 업데이트 되는 값을 set 해줌
    const trackPos = (data) => {
        setPosition({ x: data.x, y: data.y });
    };
    const [status, setStatus] = useState(false)
    const openModal = () => {
      setStatus(!status);
    };
    return(
        <>
            <div className="main_body" style={style.main_body}>
                <Draggable onDrag={(e, data) => trackPos(data)} >
                    <div className="box" onClick={openModal}>
                        <div style={style.box}>이미지 들어갈 자리</div>
                        <p style={style.p}>deco</p>
                    </div>

                </Draggable>
            </div>
            <div>
                {status ? (
                    <div onClick={openModal} style={style.set_body}>
                        <div onClick={openModal} style={style.set_box}>이미지 들어갈 자리</div>
                    </div>
                ):null}
            </div>
        </>


    )
}

const style = {
    main_body: {
        textAlign:'center',
        width: '120px',
        height: 'auto',
    },
    box: {
        width: '120px',
        height: '120px',
        backgroundColor: "red",
        margin:'0'
    },
    set_box: {
        width: '70vw',
        height: '70vh',
        backgroundColor: "blue",
        marginLeft:'50px'
    },
    set_body: {
      margin:'auto'
    },
    p: {
        margin: '0'
    }
}
