import React, {useState} from "react";
import {useHistory} from "react-router-dom";
import Draggable from "react-draggable";
import ui from "./img/ui_ux.png";

export default function InterActionDesign() {
    const history = useHistory();

    const [position, setPosition] = useState({ x: 0, y: 0 }); // box의 포지션 값
    // 업데이트 되는 값을 set 해줌
    const trackPos = (data) => {
        setPosition({ x: data.x, y: data.y });
    };

    const onClick = () => {
        history.push(`/works/InteractionDesign`);
    }

    return(
        <div style={style.main_body} >
            <Draggable onDrag={(e, data) => trackPos(data)}>
                <div className="team_ui" onDoubleClick={onClick}  onTouchStart={onClick}>
                    <img className="team_ui_img" src={ui}/>
                    <p style={style.p} className="team_ui_title">UI / UX</p>
                </div>
            </Draggable>
        </div>
    )
}

const style = {
    main_body: {
        textAlign:'center',
        width: '130px',
        height: '120px',
    },
    box: {
        width: '130px',
        height: '100px',
        backgroundColor: "red",
        margin:'0',
        borderRadius:"10%",
        // textAlign:'center',
    },
    p: {
        margin: '0',
        fontsize: '14px',
        color:"white",
        wordWrap: 'break-word'
    }
}
