import React, {useState} from "react";
import {useHistory} from "react-router-dom";
import {postWorks} from "../../components/firebase/postWorks";
import './onPost.css'
import UploadWorkImages from "../../components/works/uploadWorkImages";
import UploadProfile from "../../components/works/uploadProfile";
import OnPostView from "./onPostView";
import storage from "../../components/firebase/firebase-manager";

export default function OnPost() {

    const history = useHistory();

    const [inputs, setInputs] = useState({
        name: '',
        email : '',
        workLink: '',
        workTitle : '',
        workInfo : '',
        youtubeLink : '',

    });

    const { name, email, workLink, workTitle, workInfo, youtubeLink } = inputs; // 비구조화 할당을 통해 값 추출

    const [url, setUrl] = useState('')
    const onChange = (e) => {
        const { value, name } = e.target; // 우선 e.target 에서 name 과 value 를 추출
        setInputs({
            ...inputs, // 기존의 input 객체를 복사한 뒤
            [name]: value // name 키를 가진 값을 value 로 설정
        });
    };

    const getUrl = (e) => {
      setUrl(e.target.value);
      // console.log(setUrl)
    }

    //프로필 이미지 업로드
    const [profileImage, setProfileImage] = useState([]);
    const [profileUrl, setProfileUrl] = useState([]);
    const [profileProgress, setProfileProgress] = useState(0);

    const handleProfileChange = (e) => {
        if (e.target.files[0]) {
            setProfileImage(e.target.files[0]);
        }
    };

    const handleProfileUpload = () => {
        const uploadTask = storage.ref(`images/${profileImage.name}`).put(profileImage);
        uploadTask.on(
            "state_changed",
            (snapshot) => {
                const profileProgress = Math.round(
                    (snapshot.bytesTransferred / snapshot.totalBytes) * 100
                );
                setProfileProgress(profileProgress);
            },
            (error) => {
                console.log(error);
            },
            () => {
                storage
                    .ref("images")
                    .child(profileImage.name)
                    .getDownloadURL()
                    .then((url) => {
                        setProfileUrl(url);
                    });
            }
        );
    }

    const onPost = async () => {
        history.push(`/works/${url}`);
        await postWorks(url, inputs).then(() => {
            // GetPostingInfo(url);
            OnPostView(inputs, profileUrl);
        })
        // await GetData(url);
        // localStorage.setItem('url',url);
        // console.log(url);
    };


    return (
        <>
            <div className="works">
                <div className="student_info">
                    <div className="student_picture">
                        <progress value={profileProgress} max="100"/>
                        <br/>
                        <br/>
                        <input type="file" multiple onChange={handleProfileChange}/>
                        <button onClick={handleProfileUpload}>Upload</button>
                        <br/>
                        <br/>
                        <img
                            src={profileUrl || "http://via.placeholde.com/300"}
                            alt="firebase-image"
                            style={{width: "100px"}}
                        />
                    </div>
                    <div className="student_name">
                        <p> 학생 이름</p>
                        <input name="name" placeholder="이름" onChange={onChange} value={name}/>
                    </div>
                    <div className="student_email">
                        <p>학생 이메일</p>
                        <input name="email" placeholder="이메일" onChange={onChange} value={email}/>
                    </div>
                    <div className="student_link">
                        <p>학생 작품 링크</p>
                        <input name="workLink" placeholder="작품 링크" onChange={onChange} value={workLink}/>
                    </div>
                    <div className="post_btn" >
                        <button id="btn_toPost" onClick={onPost}>upload</button>
                    </div>
                </div>
                <div className="student_work">
                    <div className="work_title">
                        <p> 작품 제목 </p>
                        <input name="workTitle" placeholder="제목" onChange={onChange} value={workTitle}/>
                    </div>
                    <div className="work_detail">
                        <p>작품 설명</p>
                        <textarea name="workInfo" placeholder="설명" onChange={onChange} value={workInfo} id="workInfo"/>
                    </div>
                    <div className="work_link">
                        <p>작품 유튜브 링크</p>
                        <input name="youtubeLink" placeholder="유튜브" onChange={onChange} value={youtubeLink}/>
                    </div>
                    <div className="work_image">
                        <UploadWorkImages/>
                    </div>
                    <div className="select_url">
                        <p>이 작품의 주소</p>
                        <input name="url" onChange={getUrl} value={url}/>
                    </div>
                </div>
            </div>
            <div>
                <div className="none" >
                    <button >upload</button>
                </div>
            </div>
        </>
    );
}
