import React, {useState} from "react";
import {postWorks} from "../../components/firebase/postWorks";

export default function Writing() {
    const [inputs, setInputs] = useState({
        name: '',
        nickname: '',
        email : ''
    });

    const { name, nickname, email } = inputs; // 비구조화 할당을 통해 값 추출

    const onChange = (e) => {
        const { value, name } = e.target; // 우선 e.target 에서 name 과 value 를 추출
        setInputs({
            ...inputs, // 기존의 input 객체를 복사한 뒤
            [name]: value // name 키를 가진 값을 value 로 설정
        });
    };

    const onReset = () => {
        setInputs({
            name: '',
            nickname: '',
        })
    };


    return (
        <>
            <div className="student_info">
                <div className="student_picture">
                    <p>학생 사진</p>
                    <input type="file"/>
                </div>
                <div className="student_name">
                    <input placeholder="이름" onChange={onChange} value={name}/>
                </div>
                <div className="student_email">
                    <input placeholder="이메일" onChange={onChange} value={nickname}/>
                </div>
                <div className="student_link">
                    <input placeholder="인스타"/>
                </div>
            </div>
            <div className="student_work">
                <div className="work_title">
                    <input placeholder="제목"/>
                </div>
                <div className="work_detail">
                    <input placeholder="설명"/>
                </div>
                <div className="work_link">
                    <input placeholder="유튜브"/>
                </div>
                <div className="work_image">
                    <p>작품 사진</p>
                    <input type="file"/>
                </div>
            </div>
            <button onClick={onChange}>upload</button>
            <div>
                <input name="name" placeholder="이름" onChange={onChange} value={name} />
                <input name="nickname" placeholder="닉네임" onChange={onChange} value={nickname}/>
                <input name="email" placeholder="이메일" onChange={onChange} value={email}/>
                <button onClick={onReset}>초기화</button>
                <div>
                </div>
            </div>
        </>
    );
}
