import './HomeView.css';
import React from "react";

import DecoIMG from "./decoIMG";
import {Link} from "react-router-dom";
import InstaLink from "../../components/home/items/instaLink";
import YoutubeLink from "../../components/home/items/youtubeLink";

import Admin from "../../components/home/items/admin";

import TeamKorea from "../../components/home/teamKorea";
import TeamUS from "../../components/home/teamUS";

import MotionGraphics from "../../components/home/team/MotionGraphics";
import Animation from "../../components/home/team/Animation";
import VFX_SFX from "../../components/home/team/VFX_SFX";
import Game from "../../components/home/team/Game";
import InterActionDesign from "../../components/home/team/InterActionDesign";
import WebtoonConceptArt from "../../components/home/team/WebtoonConceptArt";

function HomeView() {
    const handleChange = (e) => {
        // const {name, value} = e.target.va;
        // const name="team"
        // const { value, name } = e.target; // 우선 e.target 에서 name 과 value 를 추출
        // setInputs({
        //     ...inputs, // 기존의 input 객체를 복사한 뒤
        //     [team]: e.target.value // name 키를 가진 값을 value 로 설정
            console.log(e.target);
        };
    return (
        <>
            <select onChange={handleChange}>
                <option name="team" value="MotionGraphics">Motion Graphics</option>
                <option name="team" value="Animation">Animation</option>
                <option name="team" value="VFX_SFX">VFX / SFX</option>
                <option name="team" value="Game">Game</option>
                <option name="team" value="InteractionDesign">Interaction Design</option>
                <option name="team" value="WebtoonConceptArt">Webtoon Concept Art </option>
            </select>
            <div className="hommeWrap">
                <div className="items">
                    <div className="left_items">

                        <div className="link">
                            <div className="insta_link">
                                <InstaLink/>
                            </div>
                            <div className="youtube_link">
                                <YoutubeLink/>
                            </div>
                        </div>
                        <div className="deco_image">
                            <div className="notice_image">
                                <DecoIMG/>
                            </div>
                            <div className="four_deco_image">
                                <div id="deco_1">
                                    <DecoIMG/>
                                    <DecoIMG/>
                                </div>
                                <div id="deco_2">
                                </div>
                                <div id="deco_3">
                                    <DecoIMG/>
                                    <DecoIMG/>
                                </div>
                                <div id="deco_4">
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="right_items">
                        <Admin/>
                        <div className="team" >
                            <MotionGraphics/>
                            <Animation/>
                            <VFX_SFX/>
                            <Game/>
                            <InterActionDesign/>
                            <WebtoonConceptArt/>
                            {/*<TeamKorea/>*/}
                            {/*<TeamUS/>*/}
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}
export default HomeView;
