import React, {useState, createContext, memo} from "react";
import {render} from "react-dom";
import storage from "../firebase/firebase-manager";
import OnPost from "../../views/admin/onPost";

export const ProfileContext = createContext();

const UploadProfile = memo(() => {
    const [image, setImage] = useState([]);
    const [url, setUrl] = useState([]);
    const [progress, setProgress] = useState(0);

    const initData = {
      image, setImage,
      url, setUrl,
        progress, setProgress
    }

    const handleChange = (e) => {
        if (e.target.files[0]) {
            setImage(e.target.files[0]);
        }
    };

    const handleUpload = () => {
        const uploadTask = storage.ref(`images/${image.name}`).put(image);
        uploadTask.on(
            "state_changed",
            (snapshot) => {
                const progress = Math.round(
                    (snapshot.bytesTransferred / snapshot.totalBytes) * 100
                );
                setProgress(progress);
            },
            (error) => {
                console.log(error);
            },
            () => {
                storage
                    .ref("images")
                    .child(image.name)
                    .getDownloadURL()
                    .then((url) => {
                        setUrl(url);
                    });
            }
        );
    }


    console.log("image : ", image);
    console.log("urls : ", url);

    return (
        <div>
            <ProfileContext value={initData}>
                <progress value={progress} max="100"/>
                <br/>
                <br/>
                <input type="file" multiple onChange={handleChange}/>
                <button onClick={handleUpload}>Upload</button>
                <br/>
                {/*{url}*/}
                <br/>
                <img
                    src={url || "http://via.placeholde.com/300"}
                    alt="firebase-image"
                    style={{width: "100px"}}
                />
            </ProfileContext>

        </div>
    )
})
